import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import App from './App';

let renderEntireTree = (state, addPost, updateNewPostText) => {
    const root = ReactDOM.createRoot(document.getElementById('root'));
    root.render(<React.StrictMode><App state={state} addPost={addPost} updateNewPostText={updateNewPostText}/></React.StrictMode>);
    
    
}

export default renderEntireTree
