import React from 'react'
import { ReactDOM } from 'react';
import './index.css';
import App from './App';

const root = ReactDOM.render(<React.StrictMode>
    <App state={state.state} addPost={state.addPost} addNews={state.addNews} updateNewPostText={state.updateNewPostText} />
</React.StrictMode>,
document.getElementById("root"))

export let rerenderEntireTree = (state) => {
    
    root.render(
        <React.StrictMode>
            <App state={state.state} addPost={state.addPost} addNews={state.addNews} updateNewPostText={state.updateNewPostText} />
        </React.StrictMode>
    );
};
