import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import App from './App';

const root = ReactDOM.createRoot(document.getElementById('root'));
let renderEntireTree = (state, addPost, updateNewPostText) => {
    <React.StrictMode>
        root.render(<App state={state} addPost={addPost} updateNewPostText={updateNewPostText}/>, document.getElementById('root'));
    </React.StrictMode>
    
}

export default renderEntireTree
