import React from 'react';
import {addPostActionCreator, updateNewPostTextActionCreator} from '../../../redux/Reducer/profileReducer'
import MyPosts from './MyPosts';

const MyPostsContainer = (props) => {

  let addPost = () => {
    props.state.dispatch(addPostActionCreator())
  }

  let onPostChange = (text) => {
    let action = updateNewPostTextActionCreator(text)
    props.dispatch(action)
  }

  return (
    <MyPosts posts={props.posts} updateNewPostText={()=>{onPostChange}} onAddPost={()=>{addPost}}/>
  )
}

export default MyPostsContainer;
