let state = {
    profile: {
        dialogs: [
            { id: 1, name: 'Дима' },
            { id: 2, name: 'Маша' },
            { id: 3, name: 'Паша' },
            { id: 4, name: 'Кузя' },
            { id: 5, name: 'Туся' },
            { id: 6, name: 'Патриарх' }],
        messages: [
            { id: 1, message: 'Hi' },
            { id: 2, message: 'Привет' },
            { id: 3, message: 'Hello' },
            { id: 4, message: 'Как дела?' },
            { id: 5, message: 'Whats app' }
        ],
    },
    posts: [
        { id: 1, message: 'Hi, how are you?', likesCount: '11' },
        { id: 2, message: 'It\'s my first post', likesCount: '23' }
    ]
}

export let addPost = (messagePost) => {
    let newPost = {
        id: 5,
        message: messagePost,
        likesCount: 0
    }
    state.posts.push(newPost)
    renderEntireTree()
}

export default state
