import React from 'react'
import DialogItem from './DialogItem/DialogsItem'
import Message from './Message/Message'
import {updateNewMessageBody, sendMessageCreator} from '../../redux/Reducer/dialogsReducer'
import Dialogs from './Dialogs'

const DialogsContainer = (props) => {
  let state = props.store.getState().dialogsPage

  let onSendMessageClick = () => {
    props.store.dispatch(sendMessageCreator())
  }

  let onNewMessageChange = (body) => {
    props.store.dispatch(updateNewMessageBody(body))
  }

  return (
    <Dialogs updateNewMessageBody={onNewMessageChange} sendMessage={onSendMessageClick} dialogsPage={state}/>
  )
}

export default DialogsContainer
